from sm import models# 插入菜单def create(menu_info):    if models.menu.objects.create(**menu_info):        True    return False# 更新菜单def update(menu_info):    code = menu_info['code']    if models.menu.objects.filter(code=code).update(**menu_info):        return True    return False# 根据菜单编号查询菜单信息def queryMenuByCode(code):    menu_info = models.menu.objects.filter(code=code).first()    return menu_infodef menuDelByCode(code):    try:        models.menu.objects.filter(code=code).delete()  # 按照code删除一条记录        models.menu.objects.filter(parent_code=code).delete()  # 删除所有parent_code = code 的菜单    except Exception as e:        print(e)        return False    return True